mapper.select:
/* Ingredient.getDataList */ SELECT ingcd FROM tb_ingredient WHERE useyn = 'Y'
AND iclass = #{iclass}
GROUP BY ingcd ORDER BY ingcd


/* Ingredient.getDataList */
SELECT ingnm
FROM tb_ingredient
WHERE useyn = 'Y'


    AND iclass = #{iclass}


    AND ingcd = #{ingcd}

GROUP BY ingnm
ORDER BY ingnm

/* Ingredient.getDataList */ SELECT ingcd ,iclass ,ingnm ,capacity ,useyn ,regid ,DATE_FORMAT(regdt, '%Y-%m-%d') AS regdt ,modid ,DATE_FORMAT(moddt, '%Y-%m-%d') AS moddt FROM tb_ingredient WHERE 1 = 1
AND ingcd = #{s_ingcd}
AND iclass = #{s_iclass}
AND ingnm LIKE CONCAT('%', #{s_ingnm}, '%')
AND capacity LIKE CONCAT('%', #{s_capacity}, '%')
AND useyn = #{s_useYn}
ORDER BY iclass, ingcd, ingnm
LIMIT #{offSet}, #{limit}

mapper.insert:

/* Ingredient.insData */
INSERT INTO tb_ingredient
(
     ingcd
    ,iclass
    ,ingnm
    ,capacity
    ,useyn
    ,regid
    ,regdt
    ,modid
    ,moddt
)
VALUES
    (
         #{ingcd}
        ,#{iclass}
        ,#{ingnm}
        ,#{capacity}
        ,#{useYn}
        ,#{regid}
        ,now()
        ,#{modid}
        ,now()
    )
    ON DUPLICATE KEY
UPDATE
     capacity = #{capacity}
    ,useyn = #{useYn}
    ,modid = #{modid}
    ,moddt = #{moddt}

mapper.delete:

/* Ingredient.delData */
DELETE FROM tb_ingredient
WHERE ingcd = #{ingcd}
     AND iclass = #{iclass}
     AND ingnm = #{ingnm}
