mapper.select:
/* Pharma.getDataList */ SELECT phacd ,phanm ,useyn ,regid ,regdt ,modid ,moddt FROM tb_pharma WHERE 1 = 1
AND phacd = #{s_phacd}
AND phanm LIKE CONCAT('%', #{s_phanm}, '%')
AND useyn = #{s_useYn}
ORDER BY phanm
LIMIT #{offSet}, #{limit}

mapper.insert:

/* Pharma.insData */
INSERT INTO tb_pharma
(
     phacd
    ,phanm
    ,useyn
    ,regid
    ,regdt
    ,modid
    ,moddt
)
VALUES
    (
         #{phacd}
        ,#{phanm}
        ,#{useYn}
        ,#{regid}
        ,now()
        ,#{modid}
        ,now()
    )
    ON DUPLICATE KEY
UPDATE
     phanm = #{phanm}
    ,useyn = #{useYn}
    ,modid = #{modid}
    ,moddt = #{moddt}

mapper.delete:

/* Pharma.delData */
DELETE FROM tb_pharma
WHERE phacd = #{phacd}
