mapper.select:
/* Product.getIngcdList */ SELECT ingcd FROM tb_product WHERE useyn = 'Y'
AND iclass = #{iclass}
GROUP BY ingcd ORDER BY ingcd


/* Product.getIngnmList */
SELECT ingnm
FROM tb_product
WHERE useyn = 'Y'


    AND iclass = #{iclass}


    AND ingcd = #{ingcd}

GROUP BY ingnm
ORDER BY ingnm


/* Product.getPhacdList */
SELECT A.phacd, B.phanm
FROM tb_product A
    LEFT JOIN tb_pharma B ON B.phacd = A.phacd
WHERE A.useyn = 'Y'


    AND A.iclass = #{iclass}


    AND A.ingcd = #{ingcd}


    AND A.ingnm = #{ingnm}

GROUP BY A.phacd, B.phanm
ORDER BY B.phanm, A.phacd


/* Product.getPhacdList */
SELECT prdnm
FROM tb_product
WHERE useyn = 'Y'


    AND iclass = #{iclass}


    AND ingcd = #{ingcd}


    AND ingnm = #{ingnm}


    AND phacd = #{phacd}

GROUP BY prdnm
ORDER BY prdnm

/* Product.getDataList */ SELECT A.ingcd ,A.iclass ,A.ingnm ,A.phacd ,B.phanm ,C.capacity ,A.prdnm ,A.useyn ,A.regid ,DATE_FORMAT(A.regdt, '%Y-%m-%d') AS regdt ,A.modid ,DATE_FORMAT(A.moddt, '%Y-%m-%d') AS moddt FROM tb_product A LEFT JOIN tb_pharma B ON B.phacd = A.phacd LEFT JOIN tb_ingredient C ON C.ingcd = A.ingcd AND C.iclass = A.iclass AND C.ingnm = A.ingnm WHERE 1 = 1
AND A.ingcd = #{s_ingcd}
AND A.iclass = #{s_iclass}
AND A.ingnm LIKE CONCAT('%', #{s_ingnm}, '%')
AND A.phacd = #{s_phacd}
AND A.useyn = #{s_useYn}
ORDER BY A.iclass, A.ingcd, A.ingnm
LIMIT #{offSet}, #{limit}

mapper.insert:

/* Product.insData */
INSERT INTO tb_product
(
    ingcd
    ,iclass
    ,ingnm
    ,phacd
    ,prdnm
    ,useyn
    ,regid
    ,regdt
    ,modid
    ,moddt
)
VALUES
    (
        #{ingcd}
        ,#{iclass}
        ,#{ingnm}
        ,#{phacd}
        ,#{prdnm}
        ,#{useYn}
        ,#{regid}
        ,now()
        ,#{modid}
        ,now()
    )
    ON DUPLICATE KEY
UPDATE
     useyn = #{useYn}
    ,modid = #{modid}
    ,moddt = now()

mapper.delete:

/* Product.delData */
DELETE FROM tb_product
WHERE ingcd = #{ingcd}
  AND iclass = #{iclass}
  AND ingnm = #{ingnm}
  AND phacd = #{phacd}
  AND prdnm = #{prdnm}
