mapper.select:

/* Member.getDataList */
SELECT
     userid
    ,pwd
    ,usernm
    ,level
    ,useyn
FROM tb_member
WHERE 1 = 1


    AND userid = #{s_userId}


    AND usernm LIKE CONCAT('%', #{s_userNm}, '%')


    AND level = #{s_level}


    AND useyn = #{s_useYn}

ORDER BY usernm

    LIMIT #{offSet}, #{limit}

mapper.insert:

/* Member.insData */
INSERT INTO tb_member
(
    userid
    ,pwd
    ,usernm
    ,level
    ,useyn
)
VALUES
(
     #{userId}
    ,#{pwd}
    ,#{userNm}
    ,#{level}
    ,#{useYn}
)
ON DUPLICATE KEY
UPDATE
     pwd = #{pwd}
    ,userNm  = #{userNm}
    ,level  = #{level}
    ,useyn = #{useYn}

mapper.delete:

/* Member.delData */
DELETE FROM tb_member
WHERE userid = #{userId}
